mapper.select:
select count(*)
from Store
where storeNo = #{storeNo}

select storeNo, storeName, storeCategory, ceo, companyNo, phone, imgSaveName
from Store
where storeNo = #{storeNo}

mapper.update:
update Store
set ceo = #{ceo}, companyNo = #{companyNo}, phone = #{phone}, logo = #{logo}, imgSaveName = #{imgSaveName}, imgSize = #{imgSize}
where storeNo = #{storeNo}
